vector<int> compare(vector<int> game, vector<int> guess) {
    vector<int> differences;
    for (size_t i = 0; i < game.size(); ++i) {
        int diff = abs(game[i] - guess[i]);
        differences.push_back(diff);
    }
    return differences;
}

