using namespace std;

#include <iostream>
#include <vector>
#include <string>
#include <stack>
#include <cmath>

int do_algebra(std::vector<std::string> operato, std::vector<int> operand) {
    std::stack<int> stack;

    for (size_t i = 0; i < operand.size(); ++i) {
        stack.push(operand[i]);

        if (i < operato.size()) {
            std::string op = operato[i];

            if (op == "+" || op == "-") {
                int b = stack.top();
                stack.pop();
                int a = stack.top();
                stack.pop();

                if (op == "+") {
                    stack.push(a + b);
                } else {
                    stack.push(a - b);
                }
            } else if (op == "*" || op == "//" || op == "**") {
                int b = stack.top();
                stack.pop();
                int a = stack.top();
                stack.pop();

                if (op == "*") {
                    stack.push(a * b);
                } else if (op == "//") {
                    stack.push(a / b);
                } else if (op == "**") {
                    stack.push(static_cast<int>(pow(a, b)));
                }
            }
        }
    }

    return stack.top();
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (do_algebra({"**", "*", "+"}, {2, 3, 4, 5}) == 37);
    assert (do_algebra({"+", "*", "-"}, {2, 3, 4, 5}) == 9);
    assert (do_algebra({"//", "*"}, {7, 3, 4}) == 8);
}

