vector<int> generate_integers(int a, int b) {
    // Ensure a is the smaller number and b is the larger number
    if (a > b) {
        swap(a, b);
    }

    // Initialize an empty vector to store the even digits
    vector<int> even_digits;

    // Iterate through the range from a to b
    for (int i = a; i <= b; ++i) {
        // Check if the number is an even digit
        if (i == 0 || i == 2 || i == 4 || i == 6 || i == 8) {
            even_digits.push_back(i);
        }
    }

    // Return the vector of even digits
    return even_digits;
}

