using namespace std;

// Helper function to count the number of ones in the binary representation
int countOnes(int n) {
    int count = 0;
    while (n) {
        count += n & 1;
        n >>= 1;
    }
    return count;
}

// Custom comparator for sorting
bool customComparator(int a, int b) {
    int onesA = countOnes(a);
    int onesB = countOnes(b);
    if (onesA == onesB) {
        return a < b;
    }
    return onesA < onesB;
}

vector<int> sort_array(vector<int> arr) {
    // Sort the array using the custom comparator
    sort(arr.begin(), arr.end(), customComparator);
    return arr;
}

