vector<int> sum_product(vector<int> numbers) {
    // Handle empty vector
    if (numbers.empty()) {
        return {0, 1};
    }

    // Initialize variables
    int sum = 0;
    int product = 1;

    // Calculate sum
    for (int num : numbers) {
        sum += num;
    }

    // Calculate product
    for (int num : numbers) {
        product *= num;
    }

    // Return result
    return {sum, product};
}

