vector<int> rolling_max(vector<int> numbers) {
    if (numbers.empty()) {
        return {};
    }
    if (numbers.size() == 1) {
        return numbers;
    }

    int current_max = numbers[0];
    vector<int> result;
    result.push_back(current_max);

    for (size_t i = 1; i < numbers.size(); ++i) {
        if (numbers[i] > current_max) {
            current_max = numbers[i];
        }
        result.push_back(current_max);
    }

    return result;
}

