// Function to remove spaces from the input string
string remove_spaces(const string& input) {
    string result;
    for (char c : input) {
        if (c != ' ') {
            result += c;
        }
    }
    return result;
}

// Main function to separate groups of balanced parentheses
vector<string> separate_paren_groups(string paren_string) {
    paren_string = remove_spaces(paren_string);
    vector<string> result;
    stack<char> s;
    string current_group;

    for (char c : paren_string) {
        if (c == '(') {
            s.push(c);
            current_group += c;
        } else if (c == ')') {
            s.pop();
            current_group += c;
            if (s.empty()) {
                result.push_back(current_group);
                current_group.clear();
            }
        }
    }

    return result;
}

// Helper function to compare two vectors of strings
bool compare_vectors(const vector<string>& v1, const vector<string>& v2) {
    return v1 == v2;
}

// Test function to validate the implementation
void test_separate_paren_groups() {
    assert(compare_vectors(separate_paren_groups("( ) (( )) (( )( ))"), vector<string>{"()", "(())", "(()())"}));
    assert(compare_vectors(separate_paren_groups("((())) (()) ()"), vector<string>{"((()))", "(())", "()"}));
    assert(compare_vectors(separate_paren_groups("()"), vector<string>{"()"}));
    assert(compare_vectors(separate_paren_groups(""), vector<string>{}));
    cout << "All tests passed!" << endl;
}


