// Function to check if a string is a palindrome
bool is_palindrome(const string& str) {
    string reversed(str.rbegin(), str.rend());
    return reversed == str;
}

// Function to find the shortest palindrome that begins with a supplied string
string make_palindrome(const string& str) {
    int n = str.length();
    string longest_palindromic_suffix = "";

    // Find the longest palindromic suffix
    for (int i = 0; i < n; ++i) {
        string suffix = str.substr(i);
        if (is_palindrome(suffix)) {
            longest_palindromic_suffix = suffix;
            break;
        }
    }

    // Extract the prefix before the palindromic suffix
    string prefix = str.substr(0, n - longest_palindromic_suffix.length());

    // Reverse the prefix
    string reversed_prefix(prefix.rbegin(), prefix.rend());

    // Append the reversed prefix to the end of the input string
    return str + reversed_prefix;
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (make_palindrome("") == "");
    assert (make_palindrome("x") == "x");
     assert (make_palindrome("xyz") == "xyzyx");
     assert (make_palindrome("xyx") == "xyx") ;
     assert (make_palindrome("jerry") == "jerryrrej");
}

