int how_many_times(string str, string substring) {
    // Input validation
    if (str.empty() || substring.empty()) {
        return 0;
    }

    // Initialize count
    int count = 0;

    // Iterate through the string
    for (size_t i = 0; i <= str.length() - substring.length(); ++i) {
        if (str.substr(i, substring.length()) == substring) {
            count++;
        }
    }

    // Return the count
    return count;
}

