vector<int> parse_music(string music_string) {
    vector<int> beats;
    std::istringstream iss(music_string);
    string note;
    while (iss >> note) {
        int beat = 0;
        if (note == "o") {
            beat = 4;
        } else if (note == "o|") {
            beat = 2;
        } else if (note == ".|") {
            beat = 1;
        }
        beats.push_back(beat);
    }
    return beats;
}

// Test function to verify the correctness
void test_parse_music() {
    string input = "o o| .| o| o| .| .| .| .| o o";
    vector<int> expected = {4, 2, 1, 2, 2, 1, 1, 1, 1, 4, 4};
    vector<int> result = parse_music(input);
    if (result == expected) {
        cout << "Test passed!" << endl;
    } else {
        cout << "Test failed!" << endl;
    }
}


