std::vector<float> rescale_to_unit(const std::vector<float>& numbers) {
    // Input validation
    if (numbers.size() < 2) {
        throw std::invalid_argument("Vector must contain at least two elements.");
    }

    // Find the minimum and maximum values
    float min_value = *std::min_element(numbers.begin(), numbers.end());
    float max_value = *std::max_element(numbers.begin(), numbers.end());

    // Calculate the range
    float range = max_value - min_value;

    // Apply the linear transformation
    std::vector<float> transformed_numbers;
    for (float value : numbers) {
        float new_value = (value - min_value) / range;
        transformed_numbers.push_back(new_value);
    }

    // Return the transformed vector
    return transformed_numbers;
}


