#include <iostream>

int largest_divisor(int n) {
    if (n <= 1) {
        return -1; // Handle edge case where n is 1 or less
    }

    for (int i = n - 1; i >= 1; --i) {
        if (n % i == 0) {
            return i;
        }
    }

    return -1; // This line should theoretically never be reached for n > 1
}


