std::vector<float> find_closest_elements(std::vector<float> numbers) {
    // Step 1: Input Validation
    if (numbers.size() < 2) {
        throw std::invalid_argument("Input vector must contain at least two elements.");
    }

    // Step 2: Sorting the Vector
    std::sort(numbers.begin(), numbers.end());

    // Step 3: Finding the Closest Pair
    float min_diff = std::numeric_limits<float>::max();
    std::vector<float> closest_pair(2);

    for (size_t i = 1; i < numbers.size(); ++i) {
        float diff = std::abs(numbers[i] - numbers[i - 1]);
        if (diff < min_diff) {
            min_diff = diff;
            closest_pair[0] = numbers[i - 1];
            closest_pair[1] = numbers[i];
        }
    }

    // Step 4: Returning the Result
    return closest_pair;
}

