using namespace std;

// Initialize the mapping
map<string, int> numeral_to_int = {
    {"zero", 0}, {"one", 1}, {"two", 2}, {"three", 3}, {"four", 4},
    {"five", 5}, {"six", 6}, {"seven", 7}, {"eight", 8}, {"nine", 9}
};

// Function to parse the input string
vector<string> parse_input(const string& input) {
    vector<string> words;
    string word;
    stringstream ss(input);
    while (ss >> word) {
        words.push_back(word);
    }
    return words;
}

// Function to convert numerals to integers
vector<int> convert_to_integers(const vector<string>& words) {
    vector<int> integers;
    for (const auto& word : words) {
        integers.push_back(numeral_to_int[word]);
    }
    return integers;
}

// Function to sort the integers
void sort_integers(vector<int>& integers) {
    sort(integers.begin(), integers.end());
}

// Function to convert integers back to numerals
vector<string> convert_to_numerals(const vector<int>& integers) {
    vector<string> numerals;
    for (int num : integers) {
        for (const auto& pair : numeral_to_int) {
            if (pair.second == num) {
                numerals.push_back(pair.first);
                break;
            }
        }
    }
    return numerals;
}

// Function to reconstruct the output string
string reconstruct_output(const vector<string>& numerals) {
    string result;
    for (size_t i = 0; i < numerals.size(); ++i) {
        result += numerals[i];
        if (i < numerals.size() - 1) {
            result += " ";
        }
    }
    return result;
}

// Main function to sort the numbers
string sort_numbers(const string& numbers) {
    // Parse the input
    vector<string> words = parse_input(numbers);

    // Convert numerals to integers
    vector<int> integers = convert_to_integers(words);

    // Sort the integers
    sort_integers(integers);

    // Convert integers back to numerals
    vector<string> numerals = convert_to_numerals(integers);

    // Reconstruct the output string
    return reconstruct_output(numerals);
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (sort_numbers("") == "");
    assert (sort_numbers("three") == "three");
    assert (sort_numbers("three five nine") == "three five nine");
     assert (sort_numbers("five zero four seven nine eight") == "zero four five seven eight nine");
      assert (sort_numbers("six five four three two one zero") == "zero one two three four five six");
}

