using namespace std;

string flip_case(string str) {
    if (str.empty()) {
        return "";
    }

    string result = "";
    for (char c : str) {
        if (islower(c)) {
            result += toupper(c);
        } else if (isupper(c)) {
            result += tolower(c);
        } else {
            result += c; // Non-alphabetic characters remain unchanged
        }
    }

    return result;
}


