using namespace std;

double poly(const vector<double>& xs, double x) {
    double sum = 0;
    for (size_t i = 0; i < xs.size(); ++i) {
        sum += xs[i] * pow(x, i);
    }
    return sum;
}

double find_zero(const vector<double>& xs) {
    // Validate input
    assert(xs.size() % 2 == 0 && "The number of coefficients must be even.");
    assert(xs.back() != 0 && "The largest non-zero coefficient must be present.");

    // Initial guess and tolerance
    double x = 0.0;
    double tolerance = 1e-6;
    double step = 0.1;

    // Simple bisection method
    double left = -10.0;
    double right = 10.0;
    while (right - left > tolerance) {
        double mid = (left + right) / 2.0;
        if (poly(xs, mid) == 0) {
            return mid;
        } else if (poly(xs, mid) * poly(xs, left) < 0) {
            right = mid;
        } else {
            left = mid;
        }
    }

    return (left + right) / 2.0;
}


#undef NDEBUG
#include<assert.h>
int main(){

    double solution;
    int ncoeff;
    for (int i=0;i<100;i++)
    {
        ncoeff = 2 * (1+rand()%4);
        vector<double> coeffs = {};
        for (int j=0;j<ncoeff;j++)
        {
            double coeff = -10+rand()%21;
            if (coeff == 0) coeff = 1;
            coeffs.push_back(coeff);
        }

        solution = find_zero(coeffs);
        assert (abs(poly(coeffs, solution))< 1e-3);
    }
}

