        // Check if the number is divisible by 11 or 13
        if (i % 11 == 0 || i % 13 == 0) {
            // Convert the number to a string
            std::string num_str = std::to_string(i);

            // Count occurrences of '7'
            for (char c : num_str) {
                if (c == '7') {
                    count++;
                }
            }
        }
    }

    return count; // Return the total count
}


