bool is_prime(int num) {
    if (num <= 1) return false;
    if (num == 2) return true;
    if (num % 2 == 0) return false;
    for (int i = 3; i * i <= num; i += 2) {
        if (num % i == 0) return false;
    }
    return true;
}

int fibonacci(int n) {
    if (n <= 0) return 0;
    if (n == 1) return 1;
    int a = 0, b = 1, c;
    for (int i = 2; i <= n; ++i) {
        c = a + b;
        a = b;
        b = c;
    }
    return b;
}

int prime_fib(int n) {
    int count = 0;
    int i = 1;
    std::vector<int> fib_primes;
    while (count < n) {
        int fib_num = fibonacci(i);
        if (is_prime(fib_num)) {
            fib_primes.push_back(fib_num);
            count++;
        }
        i++;
    }
    return fib_primes[n - 1];
}


