int fib4(int n) {
    // Base cases
    if (n == 0) return 0;
    if (n == 1) return 0;
    if (n == 2) return 2;
    if (n == 3) return 0;

    // Initialize the first four values
    int a = 0, b = 0, c = 2, d = 0;

    // Iterative computation
    for (int i = 4; i <= n; ++i) {
        int temp = a + b + c + d;
        a = b;
        b = c;
        c = d;
        d = temp;
    }

    // Return the result
    return d;
}


