bool pairs_sum_to_zero(vector<int> l) {
    // Handle edge case: empty vector or single element vector
    if (l.size() < 2) {
        return false;
    }

    // Initialize a hash set to store elements
    unordered_set<int> seen;

    // Iterate through each element in the vector
    for (int num : l) {
        // Check if the negation of the current element exists in the set
        if (seen.find(-num) != seen.end()) {
            return true;
        }
        // Add the current element to the set
        seen.insert(num);
    }

    // No pairs found that sum to zero
    return false;
}

