bool is_palindrome(const std::string& text) {
    // Step 1: Input Validation
    if (text.empty()) {
        return true;
    }

    // Step 2: String Length Calculation
    int len = text.length();

    // Step 3: Palindrome Check
    for (int i = 0; i < len / 2; ++i) {
        if (text[i] != text[len - i - 1]) {
            return false;
        }
    }

    // Step 4: Return Result
    return true;
}


