// Helper function for modular exponentiation
int modExp(int base, int exp, int mod) {
    int result = 1;
    base = base % mod;
    while (exp > 0) {
        if (exp % 2 == 1) {
            result = (result * base) % mod;
        }
        exp = exp >> 1;
        base = (base * base) % mod;
    }
    return result;
}

// Main function to compute 2^n % p
int modp(int n, int p) {
    if (p <= 0) {
        // Handle invalid input (e.g., throw an exception or return an error code)
        return -1; // Example error handling
    }
    int result = modExp(2, n, p);
    return result;
}

