char shift_char(char c, int shift) {
    if (c >= 'a' && c <= 'z') {
        return ((c - 'a' + shift) % 26) + 'a';
    } else if (c >= 'A' && c <= 'Z') {
        return ((c - 'A' + shift) % 26) + 'A';
    } else {
        return c; // Non-alphabetic characters remain unchanged
    }
}

char reverse_shift_char(char c, int shift) {
    if (c >= 'a' && c <= 'z') {
        return ((c - 'a' - shift + 26) % 26) + 'a';
    } else if (c >= 'A' && c <= 'Z') {
        return ((c - 'A' - shift + 26) % 26) + 'A';
    } else {
        return c; // Non-alphabetic characters remain unchanged
    }
}

string encode_shift(string s) {
    string out;
    for (char c : s) {
        out += shift_char(c, 5);
    }
    return out;
}

string decode_shift(string s) {
    string out;
    for (char c : s) {
        out += reverse_shift_char(c, 5);
    }
    return out;
}

void test_functions() {
    string test_str = "HelloWorld";
    string encoded = encode_shift(test_str);
    string decoded = decode_shift(encoded);

    cout << "Original: " << test_str << endl;
    cout << "Encoded: " << encoded << endl;
    cout << "Decoded: " << decoded << endl;

    assert(decoded == test_str);
}


