float median(std::vector<float> l) {
    if (l.empty()) {
        throw std::invalid_argument("Input vector cannot be empty.");
    }
    std::sort(l.begin(), l.end());
    int n = l.size();
    if (n % 2 == 1) {
        return l[n / 2];
    } else {
        return (l[n / 2 - 1] + l[n / 2]) / 2.0;
    }
}


