bool monotonic(vector<float> l) {
    if (l.size() <= 1) {
        return true; // A single element or empty vector is trivially monotonic
    }

    bool isIncreasing = true;
    bool isDecreasing = true;

    for (size_t i = 1; i < l.size(); ++i) {
        if (l[i] < l[i - 1]) {
            isIncreasing = false;
        }
        if (l[i] > l[i - 1]) {
            isDecreasing = false;
        }
    }

    return isIncreasing || isDecreasing;
}

