    // Check if shift exceeds the number of digits
    if (shift > len) {
        // Reverse the string and return it
        reverse(str_x.begin(), str_x.end());
        return str_x;
    }

    // Perform the circular shift
    // Calculate the effective shift (in case shift is greater than len)
    shift = shift % len;
    string shifted_str = str_x.substr(len - shift) + str_x.substr(0, len - shift);

    // Return the result
    return shifted_str;
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (circular_shift(100, 2) == "001");
    assert (circular_shift(12, 2) == "12");
    assert (circular_shift(97, 8) == "79");
    assert (circular_shift(12, 1) == "21");
    assert (circular_shift(11, 101) == "11");
}

