int fruit_distribution(string s, int n) {
    // Find the position of "apples" and "oranges"
    size_t pos_apples = s.find("apples");
    size_t pos_oranges = s.find("oranges");

    // Extract the number of apples and oranges
    string apples_str = s.substr(0, pos_apples - 1);
    string oranges_str = s.substr(pos_apples + 7, pos_oranges - pos_apples - 8);

    int apples = extract_number(apples_str);
    int oranges = extract_number(oranges_str);

    // Calculate the number of mangoes
    int mangoes = n - (apples + oranges);

    return mangoes;
}


