vector<int> pluck(vector<int> arr) {
    int smallest_even_value = INT_MAX;
    int smallest_index = -1;

    // Iterate through the vector
    for (int i = 0; i < arr.size(); ++i) {
        // Check if the current element is even
        if (arr[i] % 2 == 0) {
            // Update the smallest even value and its index
            if (arr[i] < smallest_even_value || (arr[i] == smallest_even_value && i < smallest_index)) {
                smallest_even_value = arr[i];
                smallest_index = i;
            }
        }
    }

    // Return the result
    if (smallest_index != -1) {
        return {smallest_even_value, smallest_index};
    } else {
        return {};
    }
}

