int search(vector<int> lst) {
    unordered_map<int, int> frequencyMap;

    // Calculate frequencies
    for (int num : lst) {
        frequencyMap[num]++;
    }

    int result = -1;
    // Filter and find the greatest integer
    for (const auto& pair : frequencyMap) {
        if (pair.first <= pair.second && pair.first > result) {
            result = pair.first;
        }
    }

    return result;
}

