vector<int> strange_sort_list(vector<int> lst) {
    // Step 1: Check for empty vector
    if (lst.empty()) {
        return {};
    }

    // Step 2: Sort the input vector
    sort(lst.begin(), lst.end());

    // Step 3: Initialize pointers and result vector
    int start = 0;
    int end = lst.size() - 1;
    vector<int> result;

    // Step 4: Alternate between minimum and maximum
    while (start <= end) {
        result.push_back(lst[start]);
        start++;
        if (start <= end) {
            result.push_back(lst[end]);
            end--;
        }
    }

    // Step 5: Return the result vector
    return result;
}

