bool will_it_fly(vector<int> q, int w) {
    // Check if the vector is palindromic
    int start = 0;
    int end = q.size() - 1;
    while (start < end) {
        if (q[start] != q[end]) {
            return false; // Not palindromic
        }
        start++;
        end--;
    }

    // Calculate the sum of the elements
    int sum = 0;
    for (int i = 0; i < q.size(); i++) {
        sum += q[i];
    }

    // Compare the sum with the maximum possible weight
    if (sum > w) {
        return false; // Sum exceeds the maximum weight
    }

    // Return the final result
    return true; // Palindromic and sum <= w
}

#undef NDEBUG
#include<assert.h>
int main(){
    assert (will_it_fly({3, 2, 3}, 9)==true);
    assert (will_it_fly({1, 2}, 5) == false);
    assert (will_it_fly({3}, 5) == true);
    assert (will_it_fly({3, 2, 3}, 1) == false);
    assert (will_it_fly({1, 2, 3}, 6) ==false);
    assert (will_it_fly({5}, 5) == true);
}

