bool iscube(int a) {
    // Handle special case for zero
    if (a == 0) {
        return true;
    }

    // Convert negative numbers to positive
    int abs_a = abs(a);

    // Compute the cube root
    double cube_root = cbrt(abs_a);

    // Round the cube root to the nearest integer
    int rounded_cube_root = round(cube_root);

    // Check if the cube of the rounded cube root equals the original input
    return (rounded_cube_root * rounded_cube_root * rounded_cube_root == abs_a);
}

