std::string decimal_to_binary(int decimal) {
    // Input Validation
    if (decimal < 0) {
        throw std::invalid_argument("Input must be a non-negative integer.");
    }
    if (decimal == 0) {
        return "db0db";
    }

    // Decimal to Binary Conversion
    std::string binary = "";
    while (decimal > 0) {
        binary = std::to_string(decimal % 2) + binary;
        decimal /= 2;
    }

    // String Formatting
    binary = "db" + binary + "db";

    // Return the Result
    return binary;
}


