bool is_simple_power(int x, int n) {
    // Input validation
    if (n == 0) {
        return x == 0; // Special case: 0^0 is considered true
    }
    if (n == 1) {
        return x == 1; // 1^k = 1 only if k = 0
    }

    // Logarithmic calculation
    double log_result = log(x) / log(n);
    int rounded_log_result = round(log_result);

    // Return the result
    return abs(log_result - rounded_log_result) < 1e-9; // Check if log_result is an integer
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (is_simple_power(1, 4)== true);
    assert (is_simple_power(2, 2)==true);
    assert (is_simple_power(8, 2)==true);
    assert (is_simple_power(3, 2)==false);
    assert (is_simple_power(3, 1)==false);
    assert (is_simple_power(5, 3)==false);
    assert (is_simple_power(16, 2)== true);
    assert (is_simple_power(143214, 16)== false);
    assert (is_simple_power(4, 2)==true);
    assert (is_simple_power(9, 3)==true);
    assert (is_simple_power(16, 4)==true);
    assert (is_simple_power(24, 2)==false);
    assert (is_simple_power(128, 4)==false);
    assert (is_simple_power(12, 6)==false);
    assert (is_simple_power(1, 1)==true);
    assert (is_simple_power(1, 12)==true);
}

