// Function to map GPA to letter grade
std::string get_letter_grade(float gpa) {
    if (gpa == 4.0) return "A+";
    else if (gpa > 3.7) return "A";
    else if (gpa > 3.3) return "A-";
    else if (gpa > 3.0) return "B+";
    else if (gpa > 2.7) return "B";
    else if (gpa > 2.3) return "B-";
    else if (gpa > 2.0) return "C+";
    else if (gpa > 1.7) return "C";
    else if (gpa > 1.3) return "C-";
    else if (gpa > 1.0) return "D+";
    else if (gpa > 0.7) return "D";
    else if (gpa > 0.0) return "D-";
    else return "E";
}

// Function to convert a vector of GPAs to a vector of letter grades
std::vector<std::string> numerical_letter_grade(std::vector<float> grades) {
    // Input validation
    if (grades.empty()) {
        throw std::invalid_argument("Input vector is empty.");
    }
    for (float gpa : grades) {
        if (gpa < 0.0 || gpa > 4.0) {
            throw std::invalid_argument("Invalid GPA value.");
        }
    }

    // Convert each GPA to a letter grade
    std::vector<std::string> result;
    for (float gpa : grades) {
        result.push_back(get_letter_grade(gpa));
    }

    return result;
}

