    for (int i = 2; i * i <= num; ++i) {
        if (num % i == 0) return false;
    }
    return true;
}

// Function to find the largest prime number in the list
int find_largest_prime(const vector<int>& lst) {
    int largest_prime = -1;
    for (int num : lst) {
        if (is_prime(num) && num > largest_prime) {
            largest_prime = num;
        }
    }
    return largest_prime;
}

// Function to sum the digits of a number
int sum_of_digits(int num) {
    int sum = 0;
    while (num > 0) {
        sum += num % 10;
        num /= 10;
    }
    return sum;
}

// Main function to find the largest prime and return the sum of its digits
int skjkasdkd(vector<int> lst) {
    int largest_prime = find_largest_prime(lst);
    if (largest_prime == -1) return 0; // No prime number found
    return sum_of_digits(largest_prime);
}

