using namespace std;

int is_bored(const string& S) {
    // Step 1: Parse the input string into sentences
    istringstream iss(S);
    string token;
    string delimiter = ".!?";
    vector<string> sentences;
    while (getline(iss, token, ' ')) {
        size_t pos = 0;
        while ((pos = token.find_first_of(delimiter)) != string::npos) {
            sentences.push_back(token.substr(0, pos + 1));
            token.erase(0, pos + 1);
        }
        if (!token.empty()) {
            sentences.push_back(token);
        }
    }

    // Step 2: Identify and count boredom sentences
    int boredom_count = 0;
    for (const auto& sentence : sentences) {
        // Trim leading spaces
        size_t start = sentence.find_first_not_of(" ");
        if (start == string::npos) continue; // Skip empty sentences
        string trimmed_sentence = sentence.substr(start);

        // Check if the sentence starts with "I"
        if (trimmed_sentence.size() >= 2 && trimmed_sentence[0] == 'I' && (trimmed_sentence[1] == ' ' || trimmed_sentence[1] == '.' || trimmed_sentence[1] == '?' || trimmed_sentence[1] == '!')) {
            ++boredom_count;
        }
    }

    // Step 3: Return the result
    return boredom_count;
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (is_bored("Hello world") == 0);
    assert (is_bored("Is the sky blue?") == 0);
    assert (is_bored("I love It !") == 1);
    assert (is_bored("bIt") == 0);
    assert (is_bored("I feel good today. I will be productive. will kill It") == 2);
    assert (is_bored("You and I are going for a walk") == 0);
}

