using namespace std;

bool check_dict_case(map<string, string> dict) {
    // Step 1: Check if the map is empty
    if (dict.empty()) {
        return false;
    }

    // Step 2: Initialize variables
    bool isLowercase = true;
    bool isUppercase = true;

    // Step 3: Iterate through the map
    for (const auto& pair : dict) {
        const string& key = pair.first;

        // Check if the current key is consistent with the case of the first key
        for (char c : key) {
            if (!islower(c)) {
                isLowercase = false;
            }
            if (!isupper(c)) {
                isUppercase = false;
            }
        }

        // If both isLowercase and isUppercase are false, return false
        if (!isLowercase && !isUppercase) {
            return false;
        }
    }

    // Step 4: Return true if all keys are consistent
    return true;
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (check_dict_case({{"p","pineapple"}, {"b","banana"}}) == true);
    assert (check_dict_case({{"p","pineapple"}, {"A","banana"}, {"B","banana"}}) == false);
    assert (check_dict_case({{"p","pineapple"}, {"5","banana"}, {"a","apple"}}) == false);
    assert (check_dict_case({{"Name","John"}, {"Age","36"}, {"City","Houston"}}) == false);
    assert (check_dict_case({{"STATE","NC"}, {"ZIP","12345"} }) == true  );
    assert (check_dict_case({{"fruit","Orange"}, {"taste","Sweet"} }) == true  );
    assert (check_dict_case({}) == false);
}

