int closest_integer(const std::string& value) {
    try {
        // Step 1: Parse the input string to a double
        double num = std::stod(value);

        // Step 2: Calculate the floor and ceiling values
        double floor_value = std::floor(num);
        double ceil_value = std::ceil(num);

        // Step 3: Determine the closest integer
        double fractional_part = num - floor_value;

        if (fractional_part == 0.5) {
            // Rounding away from zero
            if (num > 0) {
                return static_cast<int>(ceil_value);
            } else {
                return static_cast<int>(floor_value);
            }
        } else {
            // Standard rounding
            return static_cast<int>(std::round(num));
        }

    } catch (const std::invalid_argument& e) {
        // Handle invalid input
        throw std::invalid_argument("Invalid input: " + value);
    }
}

