std::string rounded_avg(int n, int m) {
    // Step 1: Input Validation
    if (n > m) {
        return "-1";
    }

    // Step 2: Sum Calculation
    int sum = (m * (m + 1) / 2) - ((n - 1) * n / 2);

    // Step 3: Count Calculation
    int count = m - n + 1;

    // Step 4: Average Calculation
    double average = static_cast<double>(sum) / count;

    // Step 5: Rounding
    int rounded_average = static_cast<int>(floor(average + 0.5));

    // Step 6: Binary Conversion
    std::string binary_representation = std::bitset<32>(rounded_average).to_string();
    // Remove leading zeros
    size_t first_one = binary_representation.find('1');
    if (first_one != std::string::npos) {
        binary_representation = binary_representation.substr(first_one);
    }

    return binary_representation;
}

