// Helper function to check if a number has any even digit
bool has_even_digit(int num) {
    while (num > 0) {
        int digit = num % 10;
        if (digit % 2 == 0) {
            return true;
        }
        num /= 10;
    }
    return false;
}

vector<int> unique_digits(vector<int> x) {
    vector<int> result;

    // Filter elements without even digits
    for (int num : x) {
        if (!has_even_digit(num)) {
            result.push_back(num);
        }
    }

    // Sort the filtered vector
    sort(result.begin(), result.end());

    return result;
}

