int choose_num(int x, int y) {
    // Input validation
    if (x <= 0 || y <= 0) {
        return -1;
    }

    // Determine the range
    if (x > y) {
        int temp = x;
        x = y;
        y = temp;
    }

    // Find the largest even number
    for (int i = y; i >= x; i--) {
        if (i % 2 == 0) {
            return i;
        }
    }

    // Return -1 if no even number is found
    return -1;
}


