int factorial(int i) {
    int result = 1;
    for (int j = 1; j <= i; ++j) {
        result *= j;
    }
    return result;
}

// Helper function to calculate the sum of numbers from 1 to i
int sum_to_i(int i) {
    return (i * (i + 1)) / 2;
}

// Main function to implement the requirement
vector<int> f(int n) {
    vector<int> result(n);
    for (int i = 1; i <= n; ++i) {
        if (i % 2 == 0) {
            result[i - 1] = factorial(i);
        } else {
            result[i - 1] = sum_to_i(i);
        }
    }
    return result;
}

