int count_nums(vector<int> n) {
    int count = 0; // Initialize the counter

    for (int num : n) {
        int sum = 0; // Initialize the sum of digits for the current number
        bool isNegative = num < 0; // Check if the number is negative
        num = abs(num); // Convert the number to positive for easier extraction

        while (num > 0) {
            int digit = num % 10; // Extract the last digit
            if (isNegative && num / 10 == 0) { // If it's the first digit of a negative number
                digit *= -1; // Make the first digit negative
            }
            sum += digit; // Add the digit to the sum
            num /= 10; // Remove the last digit
        }

        if (sum > 0) {
            count++; // Increment the counter if the sum is greater than 0
        }
    }

    return count; // Return the final count
}

