string exchange(vector<int> lst1, vector<int> lst2) {
    int oddCount = 0;
    int evenCount = 0;

    // Count odd elements in lst1
    for (int num : lst1) {
        if (num % 2 != 0) {
            oddCount++;
        }
    }

    // Count even elements in lst2
    for (int num : lst2) {
        if (num % 2 == 0) {
            evenCount++;
        }
    }

    // Check feasibility of exchange
    if (oddCount <= evenCount) {
        return "YES";
    } else {
        return "NO";
    }
}

