bool move_one_ball(vector<int> arr) {
    if (arr.empty()) {
        return true;
    }

    int N = arr.size();
    int breakpoint_count = 0;
    int breakpoint_index = -1;

    for (int i = 0; i < N; ++i) {
        if (arr[i] > arr[(i + 1) % N]) {
            breakpoint_count++;
            breakpoint_index = i;
        }
    }

    if (breakpoint_count > 1) {
        return false;
    }

    if (breakpoint_count == 0) {
        return true;  // The array is already sorted
    }

    // Verify the sorted sequence
    for (int i = 0; i < N - 1; ++i) {
        if (arr[(breakpoint_index + i + 1) % N] < arr[(breakpoint_index + i) % N]) {
            return false;
        }
    }

    return true;
}

#undef NDEBUG
#include<assert.h>
int main(){
    assert (move_one_ball({3, 4, 5, 1, 2})==true);
    assert (move_one_ball({3, 5, 10, 1, 2})==true);
    assert (move_one_ball({4, 3, 1, 2})==false);
    assert (move_one_ball({3, 5, 4, 1, 2})==false);
    assert (move_one_ball({})==true);
}

