vector<string> reverse_delete(string s, string c) {
    // Initialize a set to store characters from c
    set<char> charsToDelete;
    for (char ch : c) {
        charsToDelete.insert(ch);
    }

    // Initialize a string to store the result after deleting characters
    string result;
    for (char ch : s) {
        if (charsToDelete.find(ch) == charsToDelete.end()) {
            result += ch;
        }
    }

    // Check if the result string is a palindrome
    bool isPalindrome = true;
    int left = 0, right = result.length() - 1;
    while (left < right) {
        if (result[left] != result[right]) {
            isPalindrome = false;
            break;
        }
        left++;
        right--;
    }

    // Construct and return the result vector
    vector<string> resultVector;
    resultVector.push_back(result);
    resultVector.push_back(isPalindrome ? "True" : "False");
    return resultVector;
}


