string get_closest_vowel(string word) {
    // Define the set of vowels
    set<char> vowels = {'a', 'e', 'i', 'o', 'u', 'A', 'E', 'I', 'O', 'U'};

    // Initialize the result as an empty string
    string result = "";

    // Traverse the word from right to left, skipping the first and last characters
    for (int i = word.length() - 2; i > 0; --i) {
        char current_char = word[i];

        // Check if the current character is a vowel
        if (vowels.find(current_char) != vowels.end()) {
            // Check if the characters immediately before and after are consonants
            if (vowels.find(word[i - 1]) == vowels.end() && vowels.find(word[i + 1]) == vowels.end()) {
                result = current_char;
                break; // Found the closest vowel, no need to continue
            }
        }
    }

    return result;
}


