bool is_balanced(const string& s) {
    int balance = 0;
    for (char c : s) {
        if (c == '(') {
            balance++;
        } else if (c == ')') {
            balance--;
            if (balance < 0) {
                return false; // Early exit if imbalance is detected
            }
        }
    }
    return balance == 0;
}

string match_parens(vector<string> lst) {
    // Input validation
    if (lst.size() != 2) {
        throw invalid_argument("Input must be a vector of exactly two strings.");
    }
    for (const auto& str : lst) {
        for (char c : str) {
            if (c != '(' && c != ')') {
                throw invalid_argument("Strings must contain only '(' and ')' characters.");
            }
        }
    }

    // Check concatenation possibilities
    string first_concat = lst[0] + lst[1];
    string second_concat = lst[1] + lst[0];

    if (is_balanced(first_concat) || is_balanced(second_concat)) {
        return "Yes";
    } else {
        return "No";
    }
}

#undef NDEBUG
#include<assert.h>
int main(){
    assert (match_parens({"()(", ")"}) == "Yes");
    assert (match_parens({")", ")"}) == "No");
    assert (match_parens({"(()(())", "())())"}) == "No");
    assert (match_parens({")())", "(()()("}) == "Yes");
    assert (match_parens({"(())))", "(()())(("}) == "Yes");
    assert (match_parens({"()", "())"}) == "No");
    assert (match_parens({"(()(", "()))()"}) == "Yes");
    assert (match_parens({"((((", "((())"}) == "No");
    assert (match_parens({")(()", "(()("}) == "No");
    assert (match_parens({")(", ")("}) == "No");
    assert (match_parens({"(", ")"}) == "Yes");
    assert (match_parens({")", "("}) == "Yes" );
}

