bool contains_whitespace(const string& txt) {
    return txt.find(' ') != string::npos;
}

vector<string> split_on_whitespace(const string& txt) {
    vector<string> result;
    stringstream ss(txt);
    string word;
    while (ss >> word) {
        result.push_back(word);
    }
    return result;
}

bool contains_commas(const string& txt) {
    return txt.find(',') != string::npos;
}

vector<string> split_on_commas(const string& txt) {
    vector<string> result;
    stringstream ss(txt);
    string segment;
    while (getline(ss, segment, ',')) {
        result.push_back(segment);
    }
    return result;
}

int count_odd_order_lowercase(const string& txt) {
    int count = 0;
    for (char c : txt) {
        if (islower(c) && ((c - 'a') % 2 == 1)) {
            count++;
        }
    }
    return count;
}

vector<string> split_words(const string& txt) {
    if (contains_whitespace(txt)) {
        return split_on_whitespace(txt);
    } else if (contains_commas(txt)) {
        return split_on_commas(txt);
    } else {
        int count = count_odd_order_lowercase(txt);
        return {to_string(count)};
    }
}

